mapper.select:
select 

from wr_log_login
where is_delete='0' and id = #{id,jdbcType=VARCHAR}

select , wll.create_by user_name, -- wba.user_name, wsu.person_name, GROUP_CONCAT(wsr.role_name) role_name from wr_log_login wll LEFT JOIN wr_basic_auth wba ON wll.create_id=wba.id AND wba.is_delete='0' LEFT JOIN wr_sys_user wsu ON wba.sys_user_id=wsu.id AND wsu.is_delete='0' LEFT JOIN wr_sys_user_role wsur ON wsu.id=wsur.sys_user_id LEFT JOIN wr_sys_role wsr ON wsur.sys_role_id=wsr.id AND wsr.is_delete='0' where wll.is_delete='0'
AND wll.login_ip = #{loginIp,jdbcType=VARCHAR}
AND wll.login_state = #{loginState,jdbcType=CHAR}
AND wll.login_time LIKE concat('%',#{loginTimeLike,jdbcType=VARCHAR},'%')
AND wll.login_time >= #{loginTimeStart,jdbcType=VARCHAR} AND wll.login_time <= #{loginTimeEnd,jdbcType=VARCHAR}
AND wll.create_by LIKE concat('%',#{userNameLike,jdbcType=VARCHAR},'%')
AND wsu.person_name LIKE concat('%',#{personNameLike,jdbcType=VARCHAR},'%')
GROUP BY wll.id ORDER BY wll.login_time DESC
